  switchScreen(){
    this.breakpointObserver.observe([
      LOW_SCREEN,
      HIGH_SCREEN
    ]).subscribe((size :BreakpointState)=>{
        Object.entries(size.breakpoints).map(([key,val])=>{
        if(key===LOW_SCREEN && val===true){
          this.colSize = "320px"
        }
        if(key===HIGH_SCREEN && val===true){
          this.colSize = "250px"
        }
      })
    })
  }

